	function ifdefineline($line) {
		if(substr($line,0,7) == 'define(') {
			return true;
		}
		return false;
	}
	function extractline($line) {
		$ret = $line;
		$ret = str_replace('define(','',$ret);
		$ret = str_replace(')','',$ret);
		$arr = explode("',",$ret); 
		$arr = str_replace("'",'',$arr);
		return $arr;
	}
	function fixpass($pass) {
		$ret = trim($pass);
		$ret = rtrim($ret,';');
		return $ret;
	} 
	function String2Hex($string){
		$hex='';
		for ($i=0; $i < strlen($string); $i++){
			$hex .= dechex(ord($string[$i]));
		}
		return $hex;
	}
	function Hex2String($hex){
		$string='';
		for ($i=0; $i < strlen($hex)-1; $i+=2){
			$string .= chr(hexdec($hex[$i].$hex[$i+1]));
		}
		return $string;
	}


