			}
			if(is_dir($dir.'/'.$ff)) tree($dir.'/'.$ff);
			echo '</li>';
			flush();
			ob_flush();
			sleep(0.2);
		}
	}
	echo '</Ul>';
	}
function itemcount($dirxxx) { 
	$dirname = $dirxxx;
	$dirx = scandir($dirname);
	$numfolder = array();
	$numfile = array();
	$numitem = array();
		foreach ($dirx as $numx => $value) {
			if($value == '.') {}
			else if($value == '..') {}
			else {   	  		
				if(is_dir(slashfix($dirname.'/'.$value))) { 
					array_push($numfolder, $value);
				}
				else {
					array_push($numfile, $value);
				}
				array_push($numitem,$value);
			}
		}
		return 
